import React, { Component } from 'react';
import Newsitem from './Newsitem';

export class News extends Component {
    constructor() {
        super();
        this.state = {
            articles: [],
            loading: false,
            page: 1,
            totalResults: 0
        };
    }
    async componentDidMount() {
        let url = `https://newsapi.org/v2/everything?q=tesla&from=2025-02-05&sortBy=publishedAt&apiKey=53189803e5ee4be3ad1ceaa6c402e257`;
        let data = await fetch(url);
        let parsedData = await data.json();
        console.log(parsedData);
        this.setState({ articles: parsedData.articles,page:1, totalResults: parsedData.totalResults });
    }

    handleNextClick = async () => {
        console.log("next");
        if (this.state.page + 1 > Math.ceil(this.state.totalResults / 30)) {
        } else {
            let nextPage = this.state.page +1;
            let url = `https://newsapi.org/v2/everything?q=tesla&from=2025-02-05&sortBy=publishedAt&apiKey=53189803e5ee4be3ad1ceaa6c402e257&page=${this.state.page + 1}&pageSize=30`;
             this.setState({loading:true});
            let data = await fetch(url);
            let parsedData = await data.json();
            console.log(parsedData);
            this.setState({articles: parsedData.articles, page:nextPage, loading:true});
        }
    };

    
    handlePrevClick = async () => {
        console.log("previous");
        if (this.state.page - 1 > Math.ceil(this.state.totalResults / 30)) {
        } else {
            let prevPage = this.state.page - 1;

            let url = `https://newsapi.org/v2/everything?q=tesla&from=2025-02-05&sortBy=publishedAt&apiKey=53189803e5ee4be3ad1ceaa6c402e257&page=${this.state.page - 1}&pageSize=30`;
            this.setState({loading:true});
            let data = await fetch(url);
            let parsedData = await data.json();
            console.log(parsedData);
            this.setState({ articles: parsedData.articles, page:prevPage, loading:false });
        }
    };

    render() {
        return (
            <div className='container my-3'>
                <h2 className='text-center'><b>Aaj Tak-Top Headlines-News</b></h2>
                <div className="row">
                {this.state.articles?.map((element) => { 

                        return (
                            <div className='col-md-4 my-5' key={element.url}>
                                <Newsitem 
                                    title={element.title} 
                                    Description={element.description} 
                                    imageUrl={element.urlToImage}
                                    newsUrl={element.url} 
                                />
                            </div>
                        );
                    })}
                </div>
                <div className="container-3 d-flex justify-content-between">
                    <button type="button" onClick={this.handlePrevClick} disabled={this.state.page <= 1} className="btn btn-dark">&larr; previous</button>
                    <button type="button" onClick={this.handleNextClick} className="btn btn-dark">Next &rarr;</button>
                </div>
            </div>
        );
    }
}

export default News;
