// export default News;

export class News extends Component {
  articles = []; // Store fetched articles here
  constructor() {
      super();
      this.state = {
          articles: this.articles,
          loading: false  // ✅ Initialize state to prevent "null" error
      }
  }
  componentDidMount() {
    fetch("https://newsapi.org/v2/top-headlines?country=in&apiKey=53189803e5ee4be3ad1ceaa6c402e257")
    .then(response => response.json())
    .then(data => {
        this.setState({ articles: data.articles });
    });
}


  render() {
      return (
          <div className='container my-3'>
              <h2 className='text-center'><b>Aaj Tak-Top Headlines-News</b></h2>
              <div className="row">
                  {this.state.articles.map((element) => {
                      return (
                          <div className='col-md-4 my-5' key={element.url}>
                              <Newsitem 
                                  title={element.title} 
                                  Description={element.description} 
                                  imageUrl={element.urlToImage} 
                              />
                          </div>
                      );
                  })}
              </div>
       <Newsitem/>

          </div>
      );
  }
}
export default News;
