import React, { Component } from 'react';
import Newsitem from './Newsitem';
import Spinner from './Spinner'
import InfiniteScroll from "react-infinite-scroll-component";



export class News extends Component {
   
    CapitalizeFirstLetter(str) {
        return str[0].toUpperCase() + str.slice(1);
      }
      
    
    constructor(props) {
        super(props);
        this.state = { 
            articles: [],
            loading: false,
            page: 1,
            totalResults: 0
        };
        document.title = `${this.CapitalizeFirstLetter(this.props.category)} - NewsMonkey`;
    }
    //https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=636a0b4b78574390aa3f98b7e296c345
    async componentDidMount() {
        let url = `https://newsapi.org/v2/top-headlines?country=us&category=${this.props.category}&apiKey=${process.env.REACT_APP_NEWS_API_KEY}`;
        this.setState({loading: true});
        let data = await fetch(url);
        let parsedData = await data.json();
        console.log(parsedData);
        this.setState({ articles: parsedData.articles,page:1, 
                        totalResults: parsedData.totalResults,
                        loading: false});
    }
   
    
    handleNextClick = async () => {
        console.log("next");
        if (this.state.page + 1 > Math.ceil(this.state.totalResults / 24)) {
        } else {
            let nextPage = this.state.page +1;
            let url = `https://newsapi.org/v2/top-headlines?country=us&category=${this.props.category}&apiKey=${process.env.REACT_APP_NEWS_API_KEY}&page=${this.state.page + 1}&pageSize=24`;
             this.setState({loading:true});
            let data = await fetch(url);
            let parsedData = await data.json();
            console.log(parsedData);
            this.setState({articles: parsedData.articles, page:nextPage, loading:false});
        }
    };



    handlePrevClick = async () => {
        console.log("previous");
        if (this.state.page - 1 > Math.ceil(this.state.totalResults / 24)) {
        } else {
            let prevPage = this.state.page - 1;

            let url = `https://newsapi.org/v2/top-headlines?country=us&category=${this.props.category}&apiKey=${process.env.REACT_APP_NEWS_API_KEY}&page=${this.state.page - 1}&pageSize=24`;
            this.setState({loading:true});
            let data = await fetch(url);
            let parsedData = await data.json();
            console.log(parsedData);
            this.setState({ articles: parsedData.articles, page:prevPage, loading:false });
        }
    };

    render() {
        return (
            <div className='container my-3'>
                <h2 className='text-center'><b>Aaj Tak-Top Headlines-News {this.CapitalizeFirstLetter(this.props.category)}</b></h2>
               { this.state.loading && <Spinner/>}
               <InfiniteScroll
                    next={this.fetchMoreData}
                    hasMore={true}
                    dataLength={this.state.articles.length}
                    loader={<Spinner/>}>
                      <div className="container">
                              <div className="row">
                              { this.state.articles?.map((element) => { 
              
                                      return (
                                          <div className='col-md-4 my-5' key={element.url}>
                                              <Newsitem 
                                                  title={element.title} 
                                                  Description={element.description} 
                                                  imageUrl={element.urlToImage}
                                                  newsUrl={element.url} 
                                              />
                                          </div>
                                      );
                                  })}
                              </div>
                </div>
                </InfiniteScroll>
                <div className="container-3 d-flex justify-content-between">
                    <button type="button" onClick={this.handlePrevClick} disabled={this.state.page <= 1} className="btn btn-dark">&larr; previous</button>
                    <button type="button" onClick={this.handleNextClick} className="btn btn-dark">Next &rarr;</button>
                </div>
            </div>
        );
    }
}

export default News;
