import React, { Component } from 'react'
import Newsitem from './Newsitem'



export class News extends Component {

    

  constructor() {
      super();
      this.state = {
        
          articles: this.articles,
          loading: false,
          page:1 ,
          totalResults: 0
      }
  }
  async componentDidMount() {
    let url = `https://newsdata.io/api/1/news?apikey=pub_72692494882bef87a2c188de035ca6c17eccd&country=in&language=en&page=${this.state.page}&pageSize=99`;


   
        let data = await fetch(url);
        let parsedData = await data.json();
        console.log(parsedData);
        this.setState({ articles: parsedData.articles, totalResults: parsedData.totalResults });  // ✅ Correct way


    
}
// 
handleNextClick = async()=>{
console.log("next");
if(this.state.page + 1 > Math.ceil(this.state.totalResults/99)){

}
else{

    let url = `https://newsdata.io/api/1/news?apikey=pub_72692494882bef87a2c188de035ca6c17eccd&country=in&language=en&page=${this.state.page+1}&pageSize=99`;
    let data = await fetch(url);
let parsedData = await data.json()
console.log(parsedData);
this.setState({ articles: parsedData.articles,
               page: this.state.page + 1
 });

}

}
handlePrevClick = async()=>{
    console.log("previous");
    if(this.state.page - 1 > Math.ceil(this.state.totalResults/99)){

    }
    else{

    
        let url = `https://newsdata.io/api/1/news?apikey=pub_72692494882bef87a2c188de035ca6c17eccd&country=in&language=en&page=${this.state.page-1}&pageSize=99`;
        let data = await fetch(url);
let parsedData = await data.json()
console.log(parsedData);
this.setState({
                page:this.state.page - 1,
                articles: parsedData.articles });


}
}


render() {
      return (
          <div className='container my-3'>
              <h2 className='text-center'><b>Aaj Tak-Top Headlines-News</b></h2>
              <div className="row">
                  {this.state.articles.map((element) => {
                      return (
                          <div className='col-md-4 my-5' key={element.url}>
                              <Newsitem 
                                  title={element.title} 
                                  Description={element.description} 
                                  imageUrl={element.urlToImage}
                                  newsUrl={element.url} 
                              />
                          </div>
                      );
                  })}
              </div>
              <div className="container-3 d-flex justify-content-between">
                <button type="button" onClick={this.handlePrevClick} disabled={this.state.page<=1} className="btn btn-dark">&larr; previous</button>
                <button type="button" onClick={this.handleNextClick} className="btn btn-dark">Next &rarr; </button>
              </div>
              
       

          </div>
      );
  }
}
export default News
