import React, { Component } from 'react';
import Newsitem from './Newsitem';

export class News extends Component {

    constructor() {
        super();
        this.state = {
            results: [],            // ✅ Initially empty array
            loading: false,
            page: 1,
            totalResults: 0
        };
    }

    async fetchNews(page = 1) {
        const apiKey = "pub_72692494882bef87a2c188de035ca6c17eccd";
        const url = `https://newsdata.io/api/1/news?apikey=${apiKey}&country=in&language=en&page=${page}&size=9`;

        this.setState({ loading: true });

        let data = await fetch(url);
        let parsedData = await data.json();

        console.log(parsedData); // Check response structure in console

        this.setState({
            results: parsedData.results || [],   // ✅ Ensure it's an array
            totalResults: parsedData.totalResults || 0,
            page: page,
            loading: false
        });
    }

    async componentDidMount() {
        this.fetchNews(1);   // ✅ Fetch first page when component loads
    }

    handleNextClick = () => {
        if (this.state.page + 1 <= Math.ceil(this.state.totalResults / 9)) {
            this.fetchNews(this.state.page + 1);
        }
    }

    handlePrevClick = () => {
        if (this.state.page > 1) {
            this.fetchNews(this.state.page - 1);
        }
    }

    render() {
        return (
            <div className='container my-3'>
                <h2 className='text-center'><b>Aaj Tak - Top Headlines</b></h2>

                {this.state.loading && <h5 className='text-center'>Loading...</h5>}

                <div className="row">
                    {this.state.results.map((element) => {
                        return (
                            <div className='col-md-4 my-3' key={element.link}>
                                <Newsitem
                                    title={element.title ? element.title.slice(0, 45) : "No Title"}
                                    Description={element.description ? element.description.slice(0, 88) : "No Description"}
                                    imageUrl={element.image_url || 'https://via.placeholder.com/400'}
                                    newsUrl={element.link}
                                />
                            </div>
                        );
                    })}
                </div>

                <div className="d-flex justify-content-between">
                    <button disabled={this.state.page <= 1} onClick={this.handlePrevClick} className="btn btn-dark">
                        &larr; Previous
                    </button>
                    <button disabled={this.state.page + 1 > Math.ceil(this.state.totalResults / 9)} onClick={this.handleNextClick} className="btn btn-dark">
                        Next &rarr;
                    </button>
                </div>
            </div>
        );
    }
}

export default News;
