import React, { Component } from 'react';
import Newsitem from './Newsitem';
import Spinner from './Spinner';

export class News extends Component {
    constructor(props) { // ✅ props को constructor में pass करना जरूरी है
        super(props);
        this.state = {
            articles: [],
            loading: false,
            page: 1,
            totalResults: 0
        };
    }

    async componentDidMount() {
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=609a1e4de0824d8d9698ac712a1e8e6d`;
        this.setState({ loading: true }); // ✅ API call से पहले loading true कर दी
        let data = await fetch(url);
        let parsedData = await data.json();
        console.log(parsedData);
        this.setState({ 
            articles: parsedData.articles, 
            page: 1, 
            totalResults: parsedData.totalResults,
            loading: false // ✅ API call के बाद loading false कर दी
        });
    }

    // ✅ जब category या country बदले तो API को फिर से call करना जरूरी है
    componentDidUpdate(prevProps) {
        if (prevProps.category !== this.props.category || prevProps.country !== this.props.country) {
            let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=609a1e4de0824d8d9698ac712a1e8e6d`;
            this.setState({ loading: true });
            fetch(url)
                .then(response => response.json())
                .then(parsedData => {
                    this.setState({ 
                        articles: parsedData.articles, 
                        page: 1, 
                        totalResults: parsedData.totalResults,
                        loading: false 
                    });
                });
        }
    }

    handleNextClick = async () => {
        console.log("next");
        if (this.state.page + 1 > Math.ceil(this.state.totalResults / 24)) {
            // कोई बदलाव नहीं किया, क्योंकि condition पहले से सही थी
        } else {
            let nextPage = this.state.page + 1;
            let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=609a1e4de0824d8d9698ac712a1e8e6d&page=${nextPage}&pageSize=24`;
            this.setState({ loading: true }); // ✅ API call से पहले loading true
            let data = await fetch(url);
            let parsedData = await data.json();
            console.log(parsedData);
            this.setState({ articles: parsedData.articles, page: nextPage, loading: false });
        }
    };

    handlePrevClick = async () => {
        console.log("previous");
        if (this.state.page <= 1) {
            // कोई बदलाव नहीं किया, क्योंकि condition पहले से सही थी
        } else {
            let prevPage = this.state.page - 1;
            let url = `https://newsapi.org/v2/top-headlines?country&category=${this.props.category}&apiKey=609a1e4de0824d8d9698ac712a1e8e6d&page=${prevPage}&pageSize=24`;
            this.setState({ loading: true }); // ✅ API call से पहले loading true
            let data = await fetch(url);
            let parsedData = await data.json();
            console.log(parsedData);
            this.setState({ articles: parsedData.articles, page: prevPage, loading: false });
        }
    };

    render() {
        return (
            <div className='container my-3'>
                <h2 className='text-center'><b>Aaj Tak - {this.props.category.charAt(0).toUpperCase() + this.props.category.slice(1)} News</b></h2>
                
                {this.state.loading && <Spinner />} {/* ✅ अगर loading true होगी तो Spinner दिखेगा */}
                
                <div className="row">
                    {!this.state.loading && this.state.articles?.map((element) => ( 
                        <div className='col-md-4 my-5' key={element.url}>
                            <Newsitem 
                                title={element.title} 
                                Description={element.description} 
                                imageUrl={element.urlToImage}
                                newsUrl={element.url} 
                            />
                        </div>
                    ))}
                </div>
                
                <div className="container-3 d-flex justify-content-between">
                    <button type="button" onClick={this.handlePrevClick} disabled={this.state.page <= 1} className="btn btn-dark">&larr; previous</button>
                    <button type="button" onClick={this.handleNextClick} className="btn btn-dark">Next &rarr;</button>
                </div>
            </div>
        );
    }
}

export default News;
