import React, { useEffect, useState } from 'react';
import Newsitem from './Newsitem';
import Spinner from './Spinner'
import InfiniteScroll from "react-infinite-scroll-component";
// import PropTypes from 'prop-types'




const News = (props)=>  {
   
    const CapitalizeFirstLetter = (string)=> {
        return string[0].toUpperCase() + string.slice(1);
      }
      
        const [articles, setArticles] = useState([]);
        const [Loading, setLoading] = useState(true)
        const [page, setPage] = useState(1)
        const [totalResults, setTotalResults] = useState(0);

        // document.title = `${CapitalizeFirstLetter(props.category)} - NewsMonkey`;
    
    //https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=636a0b4b78574390aa3f98b7e296c345
    useEffect(()=>{
        fetchNews();
            },[])

   const  fetchNews= async()=>{
    let url = `https://newsapi.org/v2/top-headlines?country=us&category=${props.category}&apiKey=${process.env.REACT_APP_NEWS_API_KEY}&pageSize={5}`;
    let data = await fetch(url);
    setLoading(true);
    let parsedData = await data.json();
    console.log(parsedData);
    setArticles(parsedData.articles);
    setPage(page+1);
    setTotalResults(parsedData.totalResults);
    setLoading(false);

    const fetchMoreData = async()=>{
    let url = `https://newsapi.org/v2/top-headlines?country=us&category=${props.category}&apiKey=${process.env.REACT_APP_NEWS_API_KEY}&pageSize={5}`;
    setLoading(true);
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);
    setArticles(articles.concat(parsedData.articles))
    
   }
   
    


    
        return (
            <div className='container my-3'>
                <h2 className='text-center'><b>Aaj Tak-Top Headlines-News {CapitalizeFirstLetter(props.category)}</b></h2>
               {/* { state.loading && <Spinner/>} */}
               <InfiniteScroll
                    next={fetchMoreData}
                    hasMore={true}
                    dataLength={articles.length}
                    loader={<Spinner/>}>
                      <div className="container">
                              <div className="row">
                              { articles?.map((element) => { 
              
                                      return (
                                          <div className='col-md-4 my-5' key={element.url}>
                                              <Newsitem 
                                                  title={element.title} 
                                                  Description={element.description} 
                                                  imageUrl={element.urlToImage}
                                                  newsUrl={element.url} 
                                              />
                                          </div>
                                      );
                                  })}
                              </div>
                </div>
                </InfiniteScroll>
                <div className="container-3 d-flex justify-content-between">
                    <button type="button"  disabled={page <= 1} className="btn btn-dark">&larr; previous</button>
                    <button type="button"  className="btn btn-dark">Next &rarr;</button>
                </div>
            </div>
        );
    }

}
export default News;
