            const response = await fetch(url);
            const data = await response.json();
            console.log(data); // Debugging: Check if data is fetched
            this.setState({ articles: data.articles, loading: false }); // Update state with fetched articles
        } catch (error) {
            console.error("Error fetching data:", error); // Debugging: Check for errors
            this.setState({ loading: false });
        }
    }

    render() {
        const { articles, loading } = this.state;

        // Debugging: Check if articles are loaded
        console.log("Articles:", articles);

        if (loading) {
            return <div>Loading...</div>; // Show loading message
        }

        if (!articles || articles.length === 0) {
            return <div>No articles found.</div>; // Show message if no articles
        }

        return (
            <div className='my-3'>
                {articles.map((article, index) => (
                    <div className="card" style={{ width: "18rem" }} key={index}>
                        <img 
                            src={article.urlToImage || "https://via.placeholder.com/150"} // Fallback image if urlToImage is null
                            className="card-img-top" 
                            alt="..." 
                        />
                        <div className="card-body">
                            <h5 className="card-title">{article.title}</h5>
                            <p className="card-text">{article.description}</p>
                            <a href={article.url} target="_blank" rel="noopener noreferrer" className="btn-sm btn-primary">
                                Read more....
                            </a>
                        </div>
                    </div>
                ))}
            </div>
        );
    }
}

export default Newsitem;
